import ItemForm from "components/ItemForm";
import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const EditItem = ({ getCookie }) => {

    const navigate = useNavigate();
    const {id} = useParams();

    const [data, setData] = useState("");
    const [selectedFile, setSelectedFile] = useState("");

    useEffect(() => {   
        getItemDetails();
    }, []);

    const getItemDetails = async () => {

        const token = getCookie();

        let reqInstance = axios.create({
            headers: {
                Authorization: `Bearer ${token}`
            }
        });

        const url = "http://localhost:3000/api/item/" + id;
        await reqInstance.get(
            url,
            {
                withCredentials: true,
                baseURL: 'http://localhost:3000',
                
            }
        ).then(({data}) => {
            console.log("data: ", data);
            setData(data);

            const dataTransfer = new DataTransfer();

            data.imagePaths.forEach(async (item, i) => {
                const bytes = await getImg(item);
                // const bytes = base64ToArrayBuffer(base64String);
                const img = new File(bytes, "fileName");
                dataTransfer.items.add(img);
            });
    
            const files = dataTransfer.files;
    
            setSelectedFile(files);
        });
    };

    function base64ToArrayBuffer(base64) {
        var binary_string = window.atob(base64);
        var len = binary_string.length;
        var bytes = new Uint8Array(len);
        for (var i = 0; i < len; i++) {
            bytes[i] = binary_string.charCodeAt(i);
        }
        return bytes.buffer;
    }

    const getImg = async (imageUrl) => {

        const token = getCookie();

        let reqInstance = axios.create({
            headers: {
                Authorization: `Bearer ${token}`
            }
        });

        const url = "http://localhost:3000/api" + imageUrl;
        await reqInstance.get(
            url,
            {
                withCredentials: true,
                baseURL: 'http://localhost:3000',
                responseType: 'arraybuffer'
            }
        ).then(({data}) => {
            console.log("data: ", data);
            return data;
        });
    };

    const submitForm = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("name",  data.name);
        formData.append("approximateValue",  data.approximateValue);
        formData.append("locationName",  data.locationName);
        formData.append("description",  data.description);

        for (let i = 0; i < selectedFile.length; i++) {
            formData.append('imagesReferences', selectedFile[i]);
        }

        const token = getCookie();

        let reqInstance = axios.create({
            headers: {
                'ContentType': 'multipart/form-data',
                Authorization: `Bearer ${token}`
            }
        });

		try {
			const url = "http://localhost:3000/api/item/edit/" + id;
			await reqInstance.put(url, formData, 
				{
					withCredentials: true,
					baseURL: 'http://localhost:3000'
				});

			navigate("/items/mine");

		} catch (error) { 
			console.log("error: ", error);
		}
    }    

	return (
        <>
            {   
                data && selectedFile &&
                <ItemForm data={data} setData={setData} selectedFile={selectedFile} setSelectedFile={setSelectedFile} submitForm={submitForm} />
            }
        </>
	);
};

export default EditItem;
