import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ItemsList from 'components/ItemsList';
import axios from 'axios';
import { useEffect } from 'react';
import './tabs.css';

function Tabs(props) {

    const [activeTab, setActiveTab] = useState('products');
    const [items, setItems] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [orderValue, setOrderValue ] = useState(null);
    const [orderDirectionValue, setOrderDirectionValue] = useState(null);
    const [searchTextValue, setSearchTextValue] = useState(null);

    const handleTabClick = (tabName) => {
        setActiveTab(tabName);
    };

    const navigate = useNavigate();

    const clickEvent = (id) => {
        const url = '/items/' + id;
        navigate(url);
    };

    
    const getData = (controller) => {
        setIsLoading(true);
        try {
            const token = props.getCookie();
            let reqInstance = axios.create({
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            console.log("token to be sent: ", token);
            const url = "http://localhost:3000/api/tradez/" + props.id;

            reqInstance.get(
                url, 
                { 
                    signal: controller.signal
                },
                {
                    withCredentials: true,
                    baseURL: 'http://localhost:3000'
                }
            ).then(({data: res}) => { 
                setItems(res); 
                setIsLoading(false);
            });
        
        } catch (error) { 
            console.log("error: ", error);
        }
    }

    useEffect(() => {
      const vals = getData();
      console.log("vals: ", vals);
      setItems(vals);
      console.log("items: ", items);
    }, []);

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-10 col-offset-1">
          <div className="d-flex col-12">
            <button
              className={`btn btn-link btn-custom w-50 border-bottom-2 ${activeTab === 'products' ? 'active' : ''}`}
              onClick={() => handleTabClick('products')}
            >
              Products
            </button>
            <button
              className={`btn btn-link btn-custom w-50 border-bottom-2 ${activeTab === 'comments' ? 'active' : ''}`}
              onClick={() => handleTabClick('comments')}
            >
              Comments
            </button>
          </div>
          <div className="tab-content col-12">
            {activeTab === 'comments' ? (
              <div>
                {/* Display comments here */}
              </div>
            ) : (
                items != null && items != [] &&
                <div className='col-12'>
                    
                    <ItemsList 
                        clickEvent={clickEvent} getData={getData} items={items} orderValue={orderValue}
                        setOrderValue={setOrderValue} orderDirectionValue={orderDirectionValue} setOrderDirectionValue={setOrderDirectionValue}
                        searchTextValue={searchTextValue} setSearchTextValue={setSearchTextValue} isLoading={isLoading} />            
                </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Tabs;
