import React, {useState} from "react";
import Dropdown from 'react-bootstrap/Dropdown';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import Button from 'react-bootstrap/Button';
import { itemOrderByLookups } from "lookups";
import "./searchBar.css";

const SearchBar = () => {

  const [orderValue, setOrderValue ]= useState();

  return (
    <>
      <div className="mt-5 mb-5">
          <div className="d-flex col-6 offset-3 flex justify-content-between align-items-baseline">
              <input type="text" className="col-11 search-input form-control" placeholder="Search anything..."/>
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" 
                  className="col-1 search-icon bi bi-search" viewBox="0 0 16 16">
                <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z"/>
              </svg>
          </div>
      </div>
      <DropDownButton btnName={"Order By"} setDropValue={setOrderValue} list={itemOrderByLookups}/>
    </>
  );
};

const DropDownButton = ({btnName, setDropValue, list}) => {

  const [buttonName, setButtonName] = useState(btnName);

  return (
      <Dropdown as={ButtonGroup} className="">
            <Button variant="secondary" className="dropdown-button">{ buttonName }</Button>
            <Dropdown.Toggle  variant="secondary" id="dropdown-basic-button" data-bs-toggle="dropdown">
            </Dropdown.Toggle>

            <Dropdown.Menu className="dropdown-menu" aria-labelledby="dropdown-basic">
              <Dropdown.Item 
                  key={0} 
                  onClick={()=> { 
                    setDropValue(null); 
                    setButtonName("No Option"); 
                  }}>
                  No Option
              </Dropdown.Item>
              <Dropdown.Divider />
              {
                list.map((obj, i) => {
                  return (
                    <Dropdown.Item 
                      key={i+1} onClick={()=> { 
                        setDropValue(obj.id); 
                        setButtonName(obj.name); 
                      }} 
                      href="#">
                        {obj.name}
                    </Dropdown.Item>
                  )
                })
              }
            </Dropdown.Menu>
      </Dropdown>
  );
}

export default SearchBar;
