import { Container, Row, Col, Button } from 'react-bootstrap';
import axios from 'axios';
import { useEffect } from 'react';
import { useState } from 'react';

const UserProfile = ({getCookie}) => {

    const [user, setUser] = useState();

    const getUserProfile = () =>{
        console.log("entered get user profile");
        try {
            const token = getCookie();
            let reqInstance = axios.create({
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            const url = "http://localhost:3000/api/auth/profile";
            reqInstance.get(url, null,
            {
                withCredentials: true,
                baseURL: 'http://localhost:3000'
            })
            .then(({data: res}) => { 
                console.log("result: ", res);
                setUser(res);
            });

        }catch(err){
            console.log("error: ", err);
        }
    }

    useEffect(() => {
        const controller = new AbortController();
        getUserProfile(controller);
        return ()=>{
            controller.abort();
        };
    }, );

    return (
        <Container className="mt-5">
            <Row>
            <Col md={3}>
                <img src={user.imageSrc} alt="User Profile" className="img-fluid rounded-circle" />
                <Button variant="primary" className="w-100 mt-3">Logout</Button>
                <Button variant="outline-primary" className="w-100 mt-3">Forgot Password</Button>
            </Col>
            <Col md={9}>
                <h2>{user.name}</h2>
                <p>{user.email}</p>
                <p>Joined on {user.createdOn}</p>
            </Col>
            </Row>
        </Container>
    );
}

export default UserProfile;
