import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import styles from "./styles.module.css";
import {useNavigate} from 'react-router-dom';
import classnames from 'classnames';
import './styles.module.css'
const ForgotPassword = () => {
	const [email, setEmail] = useState('');
	const [error, setError] = useState("");

	const navigate = useNavigate();

	const handleChange = ({ currentTarget: input }) => {
		setEmail(input.value);
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const url = "http://localhost:3000/api/auth/login";
			const {data : res} = await axios.post(url, {email: email}, 
				{
					withCredentials: true,
					baseURL: 'http://localhost:3000'
				});

			navigate("/login");

		} catch (error) { 
			console.log("error: ", error);
			setError(JSON.parse(error.response.data).message);
		}
	};

	return (
		<div className={styles.login_container}>
			<div className={styles.login_form_container}>
				<div className={styles.left}>
					<form className={styles.form_container} onSubmit={handleSubmit}>
						<h1>Login to Your Account</h1>
						<input
							type="email"
							placeholder="someone.any@gmail.com"
							name="email"
							value={email}
                            onChange={handleChange}
							required
							className={styles.input}
						/>
						{error && <div className={styles.error_msg}>{error}</div>}
						<button type="submit" className={classnames(styles.green_btn, styles.margin_top_btn)}>
							Send Reset Link
						</button>
						<div>Login Instead ? <Link to="/login">click here</Link></div>
					</form>
				</div>
			</div>
		</div>
	);
};

export default ForgotPassword;
