import { useState, useEffect } from "react";
import axios from "axios";
import "./itemDetails.css";
import { useParams } from "react-router-dom";


const ItemDetails = ({getCookie}) => {

    const [item, setItem] = useState();
    const {id} = useParams();
    
    useEffect(() => {
        const controller = new AbortController();
        getItemDetails(controller);
        return ()=>{
            controller.abort();
        };
    }, 
    []);

    const setMainImage = (img) => {
        document.getElementById("main_image").setAttribute("src", img);
    }

    const getItemDetails = async (controller) => {
        try {
            const token = getCookie();
            let reqInstance = axios.create({
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            console.log("token to be sent: ", token);
            const url = "http://localhost:3000/api/item/" + id;
            console.log("url is: ", url);
            const {data: res} = await reqInstance.get(
                url,
                {
                    signal: controller.signal
                },
                {
                    withCredentials: true,
                    baseURL: 'http://localhost:3000'
                }
            );
    
            console.log("result: ", res);
            setItem(res);
            console.log("item: ", item);
        } catch (error) { 
            console.log("error: ", error);
        }
    }

	return (
        <>
        <div className="d-flex col-8 offset-2 outer-box flex">
            {
                item !==undefined && item.imagePaths !== undefined &&
                <div className="col-5 main-image-box m-2">
                    <img src={"http://localhost:3000" + item.imagePaths[0]} className="col-12 main-image" id="main_image"/>
                </div>
            }
            <div className="col-7 offset-1 m-2 content-box">
                <div className="col-12 d-flex flex justify-content-between align-items-center">
                    <div className="col-8 title">{item.name}</div>
                    <div className="col-4 pe-3 approximate-price">~ {item.approximateValue} S.P</div>
                </div>
                <hr/>
                <div className="col-12">{item.description}</div>
                <div className="col-12 mt-4 d-flex justify-content-around image-slider">
                    {
                        (() => {
                            let container = [];
                            {
                                item !== undefined && item !== null && item.imagePaths.forEach((data, index) => {
                                console.log("single data is: ", data);
                                container.push(
                                    <div className="each-slide col-2 border shadow-sm" key={index} onClick={()=> {
                                            const img = "http://localhost:3000" + data
                                            setMainImage(img);
                                        }}>
                                        <img className="col-12" src={"http://localhost:3000" + data} alt="Image"/>
                                    </div>
                                )
                                })
                            }
                            return container;
                        })()
                    }
                </div>
            </div>
        </div>
        </>
	);
};

export default ItemDetails;
