import { Slide } from 'react-slideshow-image';
import { useState } from 'react';
const Slideshow = ({slideImages}) => {

    console.log("SlideImages: ", slideImages);
    const [images, setImages] = useState();

    const properties = {
        duration: 5000,
        transitionDuration: 500,
        infinite: true,
        indicators: true,
        arrows: true
    }

    return (
      <Slide {...properties}>
        {
            (() => {
                setImages(slideImages);
                let container = [];
                {
                    slideImages !== undefined && slideImages !== null && slideImages.map((data, index) => {
                    console.log("single data is: ", data[index]);
                    console.log("index is: ", index);
                    container.push(
                        <div className="each-slide" key={index}>
                            <img src={"http://localhost:3000" + data[index]} alt="Image"/>
                        </div>
                    )
                    })
                }
                return container;
            })()
        }
      </Slide>
    )
};

export default Slideshow;
