import { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import {format} from 'date-fns';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./itemForm.css";
import Form from 'react-bootstrap/Form';

const ItemForm = () => {

	return (
        <>
            <div className="container d-flex col-6 offset-3 mt-5 main-container flex-column">
                <h1 className="d-flex col-12 justify-content-center mt-2 font-white">Add Item</h1>
                <hr></hr>
                <form className="d-flex col-12 flex-column">
                    <div className="d-flex mb-2 col-12 justify-content-around group" controlId="exampleForm.ControlInput1">
                        <label className="col-3 font-white">Item Name</label>
                        <input className="col-6 form-control-sm" type="text" placeholder="" />
                    </div>
                    <div className="d-flex mb-2 col-12 justify-content-around group" controlId="exampleForm.ControlInput1">
                        <label className="col-3 font-white">Approximate Value</label>
                        <input className="col-6 form-control-sm" type="number" placeholder="" />
                    </div>
                    <div className="d-flex mb-2 col-12 justify-content-around group" controlId="exampleForm.ControlInput1">
                        <label className="col-3 font-white">Location</label>
                        <input className="col-6 form-control-sm" type="text" placeholder="" />
                    </div>
                    <div className="d-flex mb-2 col-12 justify-content-around group" controlId="exampleForm.ControlInput1">
                        <label className="col-3 font-white">Item Description</label>
                        <textarea className="col-6 form-control-sm" type="textarea" placeholder="" />
                    </div>
                    <div className="d-flex mb-2 col-12 justify-content-around group" controlId="exampleForm.ControlInput1">
                        <label className="col-3 font-white">Item Description</label>
                        <input className="col-6 form-control-sm" type="file" placeholder="" multiple/>
                    </div>


                </form>
            </div>
        </>
	);
};

export default ItemForm;
