import React, { useState } from 'react';
import Item from 'components/Item';

function Tabs(props) {
  const [activeTab, setActiveTab] = useState('products');

  const handleTabClick = (tabName) => {
    setActiveTab(tabName);
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-10 col-offset-1">
          <div className="d-flex">
            <button
              className={`btn btn-link w-50 border-bottom-2 ${activeTab === 'products' ? 'active' : ''}`}
              onClick={() => handleTabClick('products')}
            >
              Products
            </button>
            <button
              className={`btn btn-link w-50 border-bottom-2 ${activeTab === 'comments' ? 'active' : ''}`}
              onClick={() => handleTabClick('comments')}
            >
              Comments
            </button>
          </div>
          <div className="tab-content">
            {activeTab === 'comments' ? (
              <div>
                {/* Display comments here */}
              </div>
            ) : (
              <div className='col-12 d-flex'>
                {
                    <div className="col-12 d-flex mt-5">
                    {
                        (() => {
                            let container = []; 
                            {
                                props.tradezItems && props.tradezItems.forEach((data, index) => {
                                console.log("single data is: ", data);
                                container.push(
                                    <Item key={index} data={data} clickEvent={()=> {props.clickEvent(data._id)}}/>
                                )
                                })
                            }
                            return container;
                        })()
                    }
                    </div>
                }
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Tabs;
