import { useState, useEffect } from "react";
import axios from "axios";
import Item from "../../components/Item";
import SearchBar from "../../components/SearchBar";
import ReactPaginate from 'react-paginate';
import "./myItems.css";


const MyItems = ({getCookie}) => {

    const [items, setItems] = useState('');
    const [orderValue, setOrderValue ] = useState(null);
    const [orderDirectionValue, setOrderDirectionValue] = useState(null);
    const [searchTextValue, setSearchTextValue] = useState(null);
    const [itemOffset, setItemOffset] = useState(0);
    
    const endOffset = itemOffset + 4;
    console.log(`Loading items from ${itemOffset} to ${endOffset}`);
    const currentItems = items.slice(itemOffset, endOffset);
    const pageCount = Math.ceil(items.length / 4);

    useEffect(() => {
        const controller = new AbortController();
        getMyItems(controller);
        return ()=>{
            controller.abort();
        };
    }, 
    [orderValue, orderDirectionValue, searchTextValue]);

    const handlePageClick = (event) => {
        const newOffset = (event.selected * 4) % items.length;
        console.log(
          `User requested page number ${event.selected}, which is offset ${newOffset}`
        );
        setItemOffset(newOffset);
      };

    const getMyItems = async (controller) => {

        try {
            const token = getCookie();
            let reqInstance = axios.create({
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            console.log("token to be sent: ", token);
            const url = "http://localhost:3000/api/item";

            const {data: res} = await reqInstance.get(
                url, 
                { 
                    params: { 
                        isMine: true,
                        order: orderValue,
                        orderDirection: orderDirectionValue,
                        searchText: searchTextValue
                    },
                    signal: controller.signal
                },
                {
                    withCredentials: true,
                    baseURL: 'http://localhost:3000'
                }
            );
    
            console.log("result: ", res);
            setItems(res);
    
        } catch (error) { 
            console.log("error: ", error);
        }
    }


	return (
    <>
        <SearchBar setOrderValue={setOrderValue} setOrderDirectionValue={setOrderDirectionValue} setSearchTextValue={setSearchTextValue} />
        <div className="d-flex flex-wrap col-10 offset-2 justify-content-start mt-4">
            {
                (() => {
                    console.log("items: ", items);
                    let container = [];
                    {
                        items !== null && items.length > 0 && items.forEach((data, index) => {
                        console.log("single data is: ", data);
                        container.push(
                            <Item key={index} data = {data} />
                        )
                        })
                    }
                    return container;
                })()
            }
        </div>
        <ReactPaginate
            breakLabel="..."
            nextLabel="next >"
            onPageChange={handlePageClick}
            pageRangeDisplayed={5}
            pageCount={pageCount}
            previousLabel="< previous"
            renderOnZeroPageCount={null}
        />
    </>
	);
};

export default MyItems;
