import React, {useState} from "react";
import "./searchBar.css";

const SearchBar = () => {

  const [categoriesValue, setCategoriesValue ]= useState();

  return (
    <>
      <div className="mt-5 mb-5">
          <div className="d-flex col-6 offset-3 flex justify-content-between align-items-baseline">
              <input type="text" className="col-11 search-input form-control" placeholder="Search anything..."/>
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" 
                  className="col-1 search-icon bi bi-search" viewBox="0 0 16 16">
                <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z"/>
              </svg>
          </div>
      </div>
      <DropDownButton btnName={"Categories"} dropValue={categoriesValue} setDropValue={setCategoriesValue} />
    </>
  );
};

const DropDownButton = ({btnName, dropValue, setDropValue}) => {

  const list = [{name: "Sports", id: 5}, {name: "Equipments", id: 6}, {name: "Electronics", id: 7}];
  return (
    <>
      <div>
          <div class="dropdown">
            <button class="btn btn-secondary" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">
              {dropValue==null || dropValue=='' ? btnName : dropValue}
            </button>
            <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
              {list.map((obj) => {
                  console.log(obj.name, obj.id);
                  return (<li><a class="dropdown-item" onClick={()=> {setDropValue(obj.id); setDropName(obj.name); }} href="#">{obj.name}</a></li>)
              })}
            </ul>
          </div>
      </div>
    </>
  );
}

export default SearchBar;
