import React from "react";
import Dropdown from "../Dropdown";
import { Link } from "react-router-dom";
import "./Item.css";

const Item = ({data}) => {

	console.log("data is: ", data);

	const deleteAction = () => {
		console.log("delete action clicked");	
	}

	return (
	<div className="container border m-1">
		<div className="d-flex justify-content-between col-12">
			<div className="col-8 p-2">
				<p className="card-title">{data.name}</p>
			</div>
			<div className="col-3 offset-2 p-2">
				<Dropdown
					icon="bx bx-dots-vertical-rounded"
					menu={
					<>
						<li className="dropdown-list">
						<Link to="/items/:id" className="dropdown-link">
							<i className="bx bx-edit dropdown-link-icon"></i>
							Edit
						</Link>
						</li>
						<li className="dropdown-list" onClick={() => deleteAction()}>
						<button className="dropdown-link">
							<i className="bx bx-trash dropdown-link-icon"></i>
							Delete
						</button>
						</li>
					</>
					}
				/>
			</div>
		</div>
		<div className="col-10 offset-1">
			<img
				src={require(`../../logo192.png`)}
				alt=""
				className="card-img"
			/>
		</div>
		<div className="d-flex col-12 justify-content-between">
			<p className="product-price">Approximate Value: {data.approximateValue}</p>
			<p className="product-stock">Tradez: 10</p>
		</div>
		<p className="product-description">Description:
		{data.description}</p>
	</div>
	);
};

export default Item;
