import { useState, useEffect } from "react";
import axios from "axios";
import "./itemDetails.css";
import { useParams } from "react-router-dom";
import Slideshow from "components/SlideShow";


const ItemDetails = ({getCookie}) => {

    const [item, setItem] = useState('');
    const [images, setImages] = useState();
    const {id} = useParams();
    
    useEffect(() => {
        getItemDetails();
    }, 
    []);

    const getItemDetails = async () => {
        try {
            const token = getCookie();
            let reqInstance = axios.create({
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            console.log("token to be sent: ", token);
            const url = "http://localhost:3000/api/item/" + id;
            console.log("url is: ", url);
            const {data: res} = await reqInstance.get(
                url, 
                {
                    withCredentials: true,
                    baseURL: 'http://localhost:3000'
                }
            );
    
            console.log("result: ", res);
            setItem(res);
    
        } catch (error) { 
            console.log("error: ", error);
        }
    }

	return (
        <>
        <div className="d-flex col-8 offset-2">
            <div className="title col-12">{item.name}</div>
            <Slideshow slideImages={item.imagePaths} />
        </div>
        </>
	);
};

export default ItemDetails;
