import { useState, useEffect } from "react";
import axios from "axios";
import Item from "../../components/Item";
import "./itemDetails.css";
import { useParams } from "react-router-dom";


const ItemDetails = ({getCookie}) => {

    const [item, setItem] = useState('');
    const {id} = useParams();
    console.log("id is: ", id);
    useEffect(() => {
        const controller = new AbortController();
        getMyItems(controller);
        return ()=>{
            controller.abort();
        };
    }, 
    []);

    const getMyItems = async (controller) => {
        try {
            const token = getCookie();
            let reqInstance = axios.create({
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            console.log("token to be sent: ", token);
            const url = "http://localhost:3000/api/item/" + id;
            console.log("url is: ", url);
            const {data: res} = await reqInstance.get(
                url, 
                { 
                    signal: controller.signal
                },
                {
                    withCredentials: true,
                    baseURL: 'http://localhost:3000'
                }
            );
    
            console.log("result: ", res);
            setItem(res);
    
        } catch (error) { 
            console.log("error: ", error);
        }
    }


	return (
        <>
        <div className="d-flex col-8 offset-2 justify-content-center">
            {
                (() => {
                    {
                        item !== null &&
                            console.log("single data is: ", item) &&
                            <Item key={1} data = {item} />
                    }
                })()
            }
        </div>
        </>
	);
};

export default ItemDetails;
