import { useState, useEffect } from "react";
import axios from "axios";
import Item from "../../components/Item";
import SearchBar from "../../components/SearchBar";
import "./myItems.css";

let abortC = null;

const MyItems = ({getCookie}) => {

    const [items, setItems] = useState('');
    const [orderValue, setOrderValue ] = useState(null);
    const [orderDirectionValue, setOrderDirectionValue] = useState(null);
    const [searchTextValue, setSearchTextValue] = useState(null);

    useEffect(async() => {
        await getMyItems();
    }, [orderValue, orderDirectionValue, searchTextValue]);

    const getMyItems = async () => {
        try {
            const token = getCookie();
            let reqInstance = axios.create({
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            console.log("token to be sent: ", token);
            const url = "http://localhost:3000/api/item";
            const {data : res} = await reqInstance.get(
                url, { params: { 
                        isMine: true,
                        order: orderValue,
                        orderDirection: orderDirectionValue,
                        searchText: searchTextValue
                } },
                {
                    withCredentials: true,
                    baseURL: 'http://localhost:3000'
                });
    
            console.log("result: ", res);
            setItems(res);
    
        } catch (error) { 
            console.log("error: ", error);
        }
    }


	return (
        <>
        <SearchBar setOrderValue={setOrderValue} setOrderDirectionValue={setOrderDirectionValue} setSearchTextValue={setSearchTextValue} />
        <div className="d-flex col-8 offset-2 justify-content-center">
            {
                (() => {
                    let container = [];
                    {
                        items !== null && items.length > 0 && items.forEach((data, index) => {
                        console.log("single data is: ", data);
                        container.push(
                            <Item key={index} data = {data} />
                        )
                        })
                    }
                    return container;
                })()
            }
        </div>
        </>
	);
};

export default MyItems;
