import React, {useState} from "react";
import Dropdown from "../Dropdown";
import "./Item.css";

const Item = ({data}) => {

	console.log("data is: ", data);
	const [openDropDown, setOpenDropDown] = useState(false);

	const deleteAction = () => {
		console.log("delete action clicked");	
	}

	return (
	<div className="container col-3 border m-1 shadow-sm">
			<div className="three-dots"
				onClick={() => {setOpenDropDown((prev) => !prev)}}>	
			</div>
			{
				openDropDown && 
				<div className="col-12">
					<Dropdown/>
				</div>
			}
		<div className="col-10 offset-1">
			<img
				src={require(`../../logo192.png`)}
				alt=""
				className="card-img product-img"
			/>
		</div>
		<div className="col-12 p-2 align-item-center">
			<p className="card-title title">{data.name}</p>
		</div>

		<div className="d-flex col-12 justify-content-between">
			<p className="product-price">Value: {data.approximateValue}</p>
			<p className="product-tradez">Tradez: 10</p>
		</div>
		<p className="product-description">{data.description}</p>
	</div>
	);
};

export default Item;
